package ru.netology.tack1;import java.util.concurrent.Callable;import java.util.concurrent.ExecutorService;import java.util.concurrent.Executors;public class Main {    public static void main(String[] args) throws InterruptedException {        ThreadGroup parent = new ThreadGroup("group parent");        System.out.println("Создаю потоки...");        Thread myThreadFirst = new MyThread(parent, ask(1));        Thread myThreadSecond = new MyThread(parent, ask(2));        Thread myThreadThird = new MyThread(parent, ask(3));        Thread myThreadFourth = new MyThread(parent, ask(4));        myThreadFirst.start();        myThreadSecond.start();        myThreadThird.start();        myThreadFourth.start();        Thread.sleep(15000);        System.out.println("Завершаю все потоки.");        parent.interrupt();    }    public static String ask(int numberThread){        return " Я поток " + numberThread + ". ";    }}